import React, { useState, useEffect, useReducer, useRef } from 'react';

import PanelResult from './PanelResult';
import appContext from './context/app-context';

const counterReducer = (state, action) => {
	switch (action.type) {
		case 'Dodaj':
			return { count: state.count + 1 };
		case 'Odejmij':
			return { count: state.count - 1 };
		default:
			console.log('Nie ma takiej opcji');
			break;
	}
};

function App() {
	const [counter, setCounter] = useState(5);
	const [title, setTitle] = useState('Hello!');

	const inputRef = useRef('Baba');

	const [state, dispatch] = useReducer(counterReducer, { count: counter });

	useEffect(() => {
		console.log(state);
		document.title = counter;
	}, [state]);

	const handleChange = () => {
		console.log(inputRef.current.value);
	};

	return (
		<>
			<appContext.Provider value={[title, setTitle]}>
				<input type='text' ref={inputRef} onChange={handleChange} />
				<div>{state.count}</div>
				<button onClick={() => dispatch({ type: 'Dodaj' })}>Dodaj</button>
				<button onClick={() => dispatch({ type: 'Odejmij' })}>Odejmij</button>
				<PanelResult />
			</appContext.Provider>
		</>
	);
}

export default App;
