import React, { useContext } from 'react';
import RandomUserContext from '../../../store/randomuser-context';
import styled, { ThemeProvider } from 'styled-components';
import themes from '../../../utils/themeStyles';
const UserWrapper = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	padding: 2em;
	background-color: ${props => props.theme.background};
	color: ${props => props.theme.color};
	border-radius: 10px;
`;

const Image = styled.img`
	margin: 1em 0;
	border-radius: 50%;
`;

UserWrapper.defaultProps = {
	themes: {
		dark: '#000',
		light: '#fff',
	},
};

const User = ({ data }) => {
	const { theme } = useContext(RandomUserContext);

	const { gender, picture, name, email } = data;

	const userName = ({ title, first, last }) => {
		return `${title} ${first} ${last}`;
	};

	return (
		<ThemeProvider theme={themes[theme]}>
			<UserWrapper>
				<h3>{userName(name)}</h3>
				<Image
					className={styled.user__img}
					src={picture.large}
					alt='random-user face'
				/>
				<p>Email : {email}</p>
				<p>Gender : {gender}</p>
			</UserWrapper>
		</ThemeProvider>
	);
};

export default User;
