import React, { useState, useEffect, useReducer } from 'react';

const counterReducer = (state, action) => {
	switch (action.type) {
		case 'Dodaj':
			state.count + 1;
			break;
		default:
			console.log('Nie ma takiej opcji')
	}
};

function App() {
	const [counter, setCounter] = useState(5);

	const [state, dispatch] = useReducer(counterReducer, { count: 2 });

	useEffect(() => {
		document.title = counter;
	}, [counter]);

	return (
		<>
			<div>{state.count}</div>
			<button onClick={() => dispatch({ type: 'Dodaj' })}>Dodaj</button>
			<button onClick={() => setCounter(counter => counter - 1)}>
				Odejmij
			</button>
		</>
	);
}

export default App;
