import React, { useContext } from 'react';
import RandomUserContext from '../../../store/randomuser-context';
import styled, { ThemeProvider } from 'styled-components';
import themes from '../../../utils/themeStyles';
const UserWrapper = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	padding: 2em;
	background-color: rgb(82, 77, 77);
	color: ${props => props.themes.theme};
	border-radius: 10px;
`;

const Image = styled.img`
	margin: 1em 0;
	border-radius: 50%;
`;

UserWrapper.defaultProps = {
    
}

const User = ({ data }) => {
	const { theme } = useContext(RandomUserContext);

	const { gender, picture, name, email } = data;

	const userName = ({ title, first, last }) => {
		return `${title} ${first} ${last}`;
	};

	return (
		<ThemeProvider theme={theme}>
			<UserWrapper>
				{console.log(theme)}
				{console.log(themes[theme].color)}
				<h3>
					{userName(name)}
					{theme}
				</h3>
				<Image
					className={styled.user__img}
					src={picture.large}
					alt='random-user face'
				/>
				<p>Email : {email}</p>
				<p>Gender : {gender}</p>
			</UserWrapper>
		</ThemeProvider>
	);
};

export default User;
