import React, { useContext } from 'react';
import { FaMedapps } from 'react-icons/fa';
import RandomUserContext from '../../../store/randomuser-context';
import styled from './Theme.module.css';

const Theme = () => {
	const context = useContext(RandomUserContext);

	return (
		<button className={styled.theme}>
			<h2>{context.theme}</h2>
			<FaMedapps className={styled.icon} />
		</button>
	);
};

export default Theme;
